// ESERCIZIO 3
function addNumbers() {
    const userNumber = prompt('Inserisci un numero');
    const total = userNumber + 12;

    console.log(`Il risultato finale è ${total}`);
}
addNumbers();
// 1. chiede all'utente un numero e successivamento dovrebbe aggiungere 12 al numero chiesto
// 2. siccome non è stato insterito parseInt prima del prompt, il numero sarà una stringa e quindi avverrà la concatenazione e non addizione


// ESERCIZIO 4
function checkAccess() {
    const addresses = ['mymail@mail.com', 'yourmail@mail.com', 'hermail@mail.com', 'hismail@mail.com'];
    const userEmail = prompt('Inserisci il tuo indirizzo email');

    let grantAccess = 'false';

    if (addresses.includes(userEmail)) {
        grantAccess = 'true';
    }

    if (grantAccess === true) {
        console.log('Accesso consentito!');
    } else {
        console.log('Accesso negato!');
    }
}
checkAccess();
// 1. chiede all'utente l'email, se l'email è presente nell'array allora consente l'accesso altrimenti lo nega
// 2. false e true sono state scritte come stringhe e non come booleano


